public class IsSubsequence {
    public static boolean isSubsequence(String s, String t) {
        int i=0,j=0;
        int n=t.length();
        int m=s.length();
        char ss[]=s.toCharArray();
        char tt[]=t.toCharArray();

        if(m<1)
            return true;

        while(i<n){
            if(tt[i]==ss[j]){
                j++;
            }
            i++;

            if(j==m)
                return true;
        }

        return false;
    }

    public static void main(String[] args) {
        String source = "";
        // System.out.println(isSubsequence("abc", source));
        // System.out.println(isSubsequence("aoc", source));
        // System.out.println(isSubsequence("acb", source));
        System.out.println(isSubsequence("leeeeetcode", source));
    }
}
